import React from 'react';
import Bar from './Bar';
import Grid from './Grid';
import axios from 'axios';


class App extends React.Component{

    constructor(props){
        super(props);

        this.TempToday = 0;
        this.TempNextDay = 0;
        this.TempNextTwoDay = 0;
    }

    onSearchSubmit = async (item) => {
        const response = await axios.get('https://eu1.locationiq.com/v1/search.php?key=fed3936b8b92f0&format=json',{
            params:{
                q:item
            }
        })

        console.log(response.data[0].lat);
        console.log(response.data[0].lon);

        const sec_response = await axios.get(`https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/1699a4e3f1c2a6cd02c6de46e9802324/${response.data[0].lat},${response.data[0].lon}`)

        console.log(sec_response);
        this.TempToday=sec_response.data.currently.temperature

        console.log(this.TempToday);

    }

    render(){
        return(
            <div className="ui container" style={{marginTop: '20px'}}>
                <Bar onSubmit={this.onSearchSubmit}/>
                <div style={{textAlign:"center"}}>
                    Current temp is 
                </div>
                <Grid />
            </div>
        );
    }
}

export default App;
