import React from 'react';

class Grid extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            low: 0,
            high: 0,
            weather: "",
            speed: "",
            date: ""
        }
    }

    componentDidUpdate = (previousProp) => {
        if (previousProp.TempLow != this.props.TempLow || previousProp.TempHigh != this.props.TempHigh || previousProp.weatherType != this.props.weatherType || previousProp.windSpeed != this.props.windSpeed) {
            this.setState({
                low: this.props.TempLow,
                high: this.props.TempHigh,
                weather: this.props.weatherType,
                speed: this.props.windSpeed,
                date: this.props.Date
            })
        }

    }

    render() {
        if (this.state.weather == "rain") {
            return (
                <div className="ui segment">
                    <div className="ui items">
                        <div className="item">
                            <div className="imgae">
                                <img src="/src/pics/rain.png" />
                            </div>
                            <div className="content">
                                <a className="header">Temperature is {this.state.low} ℃ to {this.state.high} ℃</a>
                                <div className="meta">
                                    <span>Weather type: {this.state.weather}</span><br />
                                    <span>Weed speed: {this.state.speed}</span>
                                </div>
                                <div className="description">
                                    <p></p>
                                </div>
                                <div className="extra">
                                    date: {this.state.date}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            );
        }
        else if (this.state.weather
             == "snow"){
                return (
                    <div className="ui segment">
                        <div className="ui items">
                            <div className="item">
                                <div className="imgae">
                                    <img src="/src/pics/snow.png" />
                                </div>
                                <div className="content">
                                    <a className="header">Temperature is {this.state.low} ℃ to {this.state.high} ℃</a>
                                    <div className="meta">
                                        <span>Weather type: {this.state.weather}</span><br />
                                        <span>Weed speed: {this.state.speed}</span>
                                    </div>
                                    <div className="description">
                                        <p></p>
                                    </div>
                                    <div className="extra">
                                        date: {this.state.date}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                );
             }
        else {
            return (
                <div className="ui segment">
                    <div className="ui items">
                        <div className="item">
                            <div className="imgae">
                                <img src="/src/pics/sleet.png" />
                            </div>
                            <div className="content">
                                <a className="header">Temperature is {this.state.low} ℃ to {this.state.high} ℃</a>
                                <div className="meta">
                                    <span>Weather type: {this.state.weather}</span><br />
                                    <span>Weed speed: {this.state.speed}</span>
                                </div>
                                <div className="description">
                                    <p></p>
                                </div>
                                <div className="extra">
                                    date: {this.state.date}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default Grid;
