import React from 'react';
import Bar from './Bar';
import Grid from './Grid';
import axios from 'axios';


class App extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            TempToday:0,
            TodaySituation : "",
            TempNextDayLow: 0,
            TempNextDayHigh : 0,
            WeatherTypeNextDay: "",
            WeedSpeedNextDay: "",
            TempNextTwoDayLow: 0,
            TempNextTwoDayHigh : 0,
            WeatherTypeNexTwotDay: "",
            WeedSpeedNextTwoDay: ""
        }

    }

    onSearchSubmit = async (item) => {
        const response = await axios.get('https://eu1.locationiq.com/v1/search.php?key=fed3936b8b92f0&format=json',{
            params:{
                q:item
            }
        })

        console.log(response.data[0].lat);
        console.log(response.data[0].lon);

        const sec_response = await axios.get(`https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/1699a4e3f1c2a6cd02c6de46e9802324/${response.data[0].lat},${response.data[0].lon}`)

        console.log(sec_response);
        this.setState({
            TempToday:((sec_response.data.currently.temperature - 32) * 5/9 ).toFixed(2),
            TodaySituation : sec_response.data.hourly.summary,
            TempNextDayLow : ((sec_response.data.daily.data[0].temperatureLow - 32) * 5/9 ).toFixed(2),
            TempNextDayHigh : ((sec_response.data.daily.data[0].temperatureHigh - 32) * 5/9 ).toFixed(2),
            WeatherTypeNextDay: sec_response.data.daily.data[0].precipType,
            WeedSpeedNextDay: sec_response.data.daily.data[0].windSpeed,
            TempNextTwoDayLow : ((sec_response.data.daily.data[1].temperatureLow - 32) * 5/9 ).toFixed(2),
            TempNextTwoDayHigh : ((sec_response.data.daily.data[1].temperatureHigh - 32) * 5/9 ).toFixed(2),
            WeatherTypeNexTwotDay: sec_response.data.daily.data[1].precipType,
            WeedSpeedNextTwoDay : sec_response.data.daily.data[1].windSpeed
        });

        console.log(this.state.WeatherTypeNextDay);
        console.log(this.state.WeedSpeedNextDay);

    }

    render(){
        return(
            <div className="ui container" style={{marginTop: '20px'}}>
                <Bar onSubmit={this.onSearchSubmit}/>
                <div style={{textAlign:"center"}}>
                    Today's Temperature is {this.state.TempToday} ℃
                </div>
                <div style={{textAlign:"center"}}>
                    {this.state.TodaySituation}
                </div>
                <Grid TempLow={this.state.TempNextDayLow} TempHigh={this.state.TempNextDayHigh} weatherType={this.state.WeatherTypeNextDay} windSpeed={this.state.WeedSpeedNextDay}/>
                <Grid TempLow={this.state.TempNextTwoDayLow} TempHigh={this.state.TempNextTwoDayHigh} weatherType={this.state.WeatherTypeNextTwoDay} windSpeed={this.state.WeedSpeedNextTwoDay}/>
            </div>
        );
    }
}

export default App;
